class Solution:
    def minimumSteps(self, s: str) -> int:
        steps = 0
        count_ones = 0
        for char in s:
            if char == '1':
                count_ones += 1
            elif char == '0':
                steps += count_ones

        return steps


bw = Solution()
t_start = time.time_ns()
print("Resultado = " + str(bw.minimumSteps(s="")))
t_end = time.time_ns()
